import { createContext, useContext, useState, useEffect } from "react";
import { USERS } from "../data/dummyData";

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check local storage for persisted user
    const storedUser = localStorage.getItem("dayflow_user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  const login = (email, password) => {
    const foundUser = USERS.find((u) => u.email === email && u.password === password);
    if (foundUser) {
      setUser(foundUser);
      localStorage.setItem("dayflow_user", JSON.stringify(foundUser));
      return { success: true, role: foundUser.role };
    }
    return { success: false, message: "Invalid email or password" };
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("dayflow_user");
  };

  const register = (userData) => {
      // In a real app we would add to DB. Here we can just mock success.
      // For the scope of this frontend-only task, we won't permanently add to USERS array
      // but we can simulate a successful registration.
      return { success: true, message: "Registration successful! Please login." };
  }

  const value = {
    user,
    login,
    logout,
    register,
    loading
  };

  return <AuthContext.Provider value={value}>{!loading && children}</AuthContext.Provider>;
};

